class Solution(object):
    def countSubarrays(self, nums, k):
        count = 0
        n = len(nums)
        and_map = defaultdict(int)

        for i in range(n):
            new_and_map = defaultdict(int)

            if nums[i] == k:
                count += 1
            new_and_map[nums[i]] += 1

            for val in and_map:
                new_val = val & nums[i]
                if new_val == k:
                    count += and_map[val]
                new_and_map[new_val] += and_map[val]

            and_map = new_and_map

        return count
