class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:

        n= len(nums)
        f= dict()

        ans = 0
        for i in range(n):
            newf= dict()

            if nums[i]== k:
                ans+=1

            newf[nums[i]]=1

            for ke, v in f.items():
                temp =  nums[i] & ke
                if temp== k:
                    ans+= v

                if temp in newf:
                    newf[temp]+= v
                else:
                    newf[temp]= v

            f= newf

        return ans
