class Solution {
public:
    long long countSubarrays(vector<int>&arr, int k) {
        int n=arr.size();
        long long t=0;
        map<long long,long long> mp1;
        for(int i=0;i<n;i++){
            map<long long,long long> mp2;
            if(arr[i]==k){
                t++;
            }
            mp2[arr[i]] = 1;
            for (auto& [v , f] : mp1) {
                long long newans = v & arr[i];
                if (newans == k) {
                    t += f;
                }
                mp2[newans]+=f;
            }
            mp1=mp2;
        }
        return t;
    }
};
