class Solution {
public:
    bool compare(int &a , int &b){
        return a>b;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        map<long long, long long> mpr;

        long long answer = 0;
        bool cmp = false;
        int take_down = 0;

        for (int j = 0; j < size; j++) 
        {
            map<long long, long long> crs; 
            if (nums[j] == k) answer+= 1;
            if(cmp == false){
                cmp = true;
                take_down--;
            }
            else cmp = true;

            crs[nums[j]] = 1;

            for (auto &[v, f] :mpr) {
                long long res = (v & nums[j]);
                if (res == k)answer += f;
                crs[res] += f;
            }
            // cmp(a,b)

            take_down++;
            cmp = true;
            mpr = crs;
        }
        return answer;
    }
};
