class Solution(object):
    def countSubarrays(self, nums, k):
        """
        :type nums: List[int]
        :type k: int
        :rtype: int
        """
        n = len(nums)
        l = 0;
        b = {}

        for i in range(n):
            # sf
            tb = {}
            if nums[i] == k:
                l+=1
            tb[nums[i]]=1
            # sdf
            for c,g in b.items():
                # sdf
                ct = c&nums[i]
                if ct==k:
                    # sef
                    l+=g
                    # sdf
                if ct in tb:
                    tb[ct] += g
                else:
                    # asdf
                    tb[ct] = g
            b = tb
        return l
