class Solution {
    public long countSubarrays(int[] nums, int k) {

     long cnt = 0;
        Map<Long, Long> map = new TreeMap<>();

        for (int val : nums) {
            TreeMap<Long, Long> map2 = new TreeMap<>();

            if (val == k) {
                ++cnt;
            }
            map2.put((long) val, 1L);

            for (Map.Entry<Long, Long> e : map.entrySet()) {
                long count = e.getValue();
                long andRes = e.getKey() & val;
                if (andRes == k) {
                    cnt += count;
                }
                map2.put(andRes, map2.getOrDefault(andRes, 0L) + count);
            }
            map = map2;
        }

        return cnt;
    }

}
