#define ll long long

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        map<ll, ll> mp;
        ll ans=0;

        for(int i=0;i<n;i++) {
            //new map
            map<ll, ll> mp2;

            for(auto a : mp) {
                int val=a.first&nums[i];
                mp2[val]+=a.second;
            }

            mp2[nums[i]]++;
            mp=mp2;

            ans+=mp[k];
        }

        return ans;
    }
};
