class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
       int n = nums.size();
        long long ans = 0;

        unordered_map<long long, long long> prevRes;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> currRes; 

            if (nums[i] == k) {
                ++ans;
            }
            currRes[nums[i]] = 1;

            for (auto& [val, freq] : prevRes) {
                long long newAndResult = val & nums[i];
                if (newAndResult == k) {
                    ans += freq;
                }
                currRes[newAndResult] += freq;
            }

            prevRes = currRes;
        }

        return ans; 
    }
};
