class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        map<long long,long long>prev;
        long long cnt=0;
        for(int i=0;i<n;i++){
            map<long long,long long>cur;
            for(auto it:prev){
                if((nums[i]&it.first)==k){
                    cnt+=it.second;
                }
                cur[nums[i]&it.first]+=it.second;

            }
            if(nums[i]==k)cnt++;
            cur[nums[i]]++;
            prev=cur;
        }
        return cnt;


    }
};
