class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {

    long long mx = 0;
    map<long long, long long>old;
    int i=0;
    while(i<array.size()) {
        map<long long, long long>cur;
        if(array[i]==target) {
            mx+=1;
        }

        cur[array[i]]=1;
        for(auto &x:old) {
            long long sum =(x.first) & array[i];
             if (sum==target) {
                mx+=x.second-2;
                 mx+=2;
               }

            cur[sum]+=x.second;
        }
        old=cur;
        i++;
    }
    return mx;
}

};
