class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = (int)a.size();
        long long answer = 0;
        map<int, int> mmap;
        for (int i = n - 1; i >= 0; i--) {
            map<int, int> new_mmap;
            for (auto [key, val]: mmap) {
                new_mmap[key & a[i]] += val;
            }
            mmap = new_mmap;
            mmap[a[i]]++;
            answer += mmap[k];
        }
        return answer;
    }
};
