class Solution {
public:
    bool issuper(int num, int k) {
        for (int i = 0; i < 32; i++) {
            if ((1 << i) & k) {
                if (((1 << i) & num) == 0) {
                    return false;
                }
            }
        }
        return true;
    }

    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int l = 0;
        vector<int> zeros(32, 1e9);
        for (int r = 0; r < nums.size(); r++) {
            int x = nums[r];
            if (!issuper(x, k)) {
                l = r + 1;
                for (auto &i : zeros) i = 1e9;
                continue;
            }

            for (int i = 0; i < 32; i++) {
                if ((x & (1 << i)) == 0) {
                    zeros[i] = r;
                }
            }

            int maxi = 1e9;
            for (int i = 0; i < 32; i++) {
                if ((k & (1 << i)) == 0) {
                    maxi = min(maxi, zeros[i]);
                }
                if ((k & (1 << i)) == 0 && zeros[i] == 1e9) {
                    maxi = 1e9;
                    break;
                }
            }
            cout << l << " " << r << " " << maxi << endl;
            if (maxi < 1e9) ans += (long long)maxi - l + 1;
        }
        return ans;
    }
};
