class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        c=0
        n = len(nums)
        dic = {}
        for i in range(n):
            lst1 = {}
            if nums[i] == k:
                c += 1
            lst1[nums[i]] = 1
            for val, freq in dic.items():
                temp_res = val & nums[i]
                if temp_res == k:
                    c += freq
                if temp_res in lst1:
                    lst1[temp_res] += freq
                else:
                    lst1[temp_res] = freq
            dic = lst1
        return c
