class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long ans = 0;
        map<int,long long> mp;
        map<int,long long> mp2;
        for(int i=0;i<n;i++){
            for(const auto&[key,val] : mp){
                int res = key&nums[i];
                if(res==k) ans+=val;
                if(res>=k){
                    mp2[res] += val;
                }
            }
            mp = mp2;
            mp2.clear();
            if(nums[i] == k) ans++;
            if(nums[i] >= k) mp[nums[i]]++;
            else{
                mp.clear();
            }
        }
        return ans;
    }
};
