func countSubarrays(nums []int, k int) int64 {
    ans := 0
    type pair struct {a, cnt int}
	and := []pair{} 
	for _, x := range nums {
        and = append(and, pair{x, 1})
		and[0].a &= x
		j := 0
		for _, p := range and[1:] {
			p.a &= x
			if and[j].a == p.a {
				and[j].cnt += p.cnt
			} else {
				j++
				and[j] = p
			}
		}
		and = and[:j+1] 
        for _, p := range and {
            if p.a == k {
                ans += p.cnt
            }
        }

	}

    return int64(ans)
}
