class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        unordered_map<int, long long> mp;

        long long count = 0;

        for (int n : nums) {

            unordered_map<int, long long> newMp;

            if (n == k) {
                count++;
            }

            for (auto m : mp) {

                int x = m.first & n;

                if (x == k) {
                    count = count + m.second;
                }

                newMp[x] = newMp[x] + m.second;
            }

            newMp[n]++;

            mp = newMp;
        }

        return count;

    }
};
