from typing import List


class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n=len(nums)
        res=0
        bs=[]
        for i in range(n):
            nbs=[[nums[i],i]]
            for v,j in bs:
                if (nums[i]&v)!=nbs[-1][0]:
                    nbs.append([(nums[i]&v), j])
                else:
                    nbs[-1][1]=j
            for j in range(len(nbs)):
                if nbs[j][0]==k:
                    res+=(i+1-nbs[j][1]) if j==0 else nbs[j-1][1]-nbs[j][1]
            bs=nbs
        return res
