class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;
        unordered_map<long long,long long>curr;
        unordered_map<long long, long long>prev;
        for(int i=0; i<n; i++){
            // vector<pair<int,int>>v;
            if(nums[i]==k){
                cnt++;
            }
            // v.push_back({nums[i],1});
            curr[nums[i]] = 1;
            for(auto x:prev){
                // cout<<x.first<<" "<<x.second<<" ";
                if((x.first & nums[i])==k){
                    cnt += x.second;
                }
                curr[nums[i]&x.first] += x.second;
            }
            // cout<<endl;
            prev = curr;
            curr.clear();
        }
        return cnt;
    }
};
