class Solution {
    public long countSubarrays(int[] arr, int target) {
        long total = 0;int i = 0;
        Map<Long, Long> prevRes = new HashMap<>();
        while (i < arr.length) {
            Map<Long, Long> currRes = new HashMap<>();
            if (arr[i] == target)++total;
            currRes.put((long) arr[i], 1L);
            Iterator<Map.Entry<Long, Long>> iter = prevRes.entrySet().iterator();
            while (iter.hasNext()) {
                Map.Entry<Long, Long> entry = iter.next();
                long val = entry.getKey(),freq = entry.getValue(),newAndRes = val & arr[i];
                if (newAndRes == target)total += freq;
                currRes.put(newAndRes, currRes.getOrDefault(newAndRes, 0L) + freq);}
            prevRes = currRes;
            i++;}    
        return total;}}
