class Solution {
public:
long long int solve(vector<int>& nums, int k){
    int n=nums.size();
  vector<int>ct(64,0);

        int j=0;
          long long int ans=0;
           for(int r=0;r<64;++r){
                if(nums[0] &  (1LL<<r)){
                    ct[r]++;
                }
               }
        for(int i=0;i<n;++i){ 


            if(j<i){
                j=i;

                for(int r=0;r<64;++r){
                if(nums[j] &  (1LL<<r)){
                    ct[r]++;
                }
               }

            }




              long long int p=0;
              for(int r=0;r<64;++r){
                if(ct[r]==(j-i)+1){
                    p|=(1LL<<r);
                }
              }

              while(j<n && p>k){
                j++;
                if(j>=n)break;
               for(int r=0;r<64;++r){
                if(nums[j] &  (1LL<<r)){
                    ct[r]++;
                }
               }
                p=0;

              for(int r=0;r<64;++r){
                if(ct[r]==(j-i)+1){
                    p|=(1LL<<r);
                }
              }

              }

              if(p <=k){
                ans+= (n-j);
              }else{
                // if(j>=n)break;
              }
            for(int r=0;r<64;++r){
                if(nums[i] &  (1LL<<r)){
                    ct[r]--;
                }
               }

        }
        return ans;
}
    long long countSubarrays(vector<int>& nums, int k) {
        long long int sum1=solve(nums,k);
        long long int sum2=solve(nums,k-1);
        long long int ans=sum1-sum2;
        return ans;

    }
};
