class Solution {
public:
    long long countSubarrays(vector<int> &hnums,int hk){
        int hn=hnums.size();
        long long hcount=0;
        unordered_map<int,int> hprev;
        for(int i=0;i<hn;i++){
            unordered_map<int,int> hcurr;
            for(auto &[hval,hcnt]:hprev){
                int hnewAnd=hval&hnums[i];
                hcurr[hnewAnd]+=hcnt;
            }
            hcurr[hnums[i]]++;
            if(hcurr.count(hk)){
                hcount+=hcurr[hk];
            }
            hprev=hcurr;
        }
        return hcount;
    }
};
