class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long n = nums.size();
        long long ans = 0;
        unordered_map<long long, long long> res;
        for(long long i = 0; i < n; i++)
        {
            if(nums[i] == k)
            {
                ans++;
            }
            unordered_map<long long, long long> m;
            m[nums[i]] = 1;
            for(auto x : res)
            {
                long long temp = x.first & nums[i];
                if(temp == k)
                {
                    ans += x.second;
                }
                m[temp] += x.second;
            }
            res = m;
        }
        return ans;
    }
};
