class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n=a.size();
        vector<vector<int>> cnt(n+1,vector<int>(30,0));
        for(int i=1;i<=n;i++){
            for(int j=0;j<30;j++) cnt[i][j]=cnt[i-1][j]+(((1ll<<j)&a[i-1])?1:0);
        }
        long long int ans=0;
        for(int i=1;i<=n;i++){

            int l=-1,r=-1;
            int low=i,high=n;

            while(low<=high){
                int m=(low+high)/2;
                long long int v=0;
                for(int j=0;j<30;j++){
                    if((cnt[m][j]-cnt[i-1][j])==(m-i+1)) v+=(1ll<<j);
                }
                if(v>k){
                    low=m+1;
                }
                else if(v==k){
                    l=m;
                    high=m-1;
                }
                else high=m-1;
            }

            low=i,high=n;
            while(low<=high){
                int m=(low+high)/2;
                long long int v=0;
                for(int j=0;j<30;j++){
                    if((cnt[m][j]-cnt[i-1][j])==(m-i+1)) v+=(1ll<<j);
                }
                if(v>k){

                    low=m+1;
                }
                else if(v==k){
                    r=m;
                    low=m+1;
                }
                else high=m-1;
            }

            if(l==-1) continue;
            ans+=r-l+1;



        }

        return ans;
    }
};
