class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int size = array.size();
        long long total = 0;
        unordered_map<long long, long long> prev;

        int i = 0;
        while (i < size) {
            unordered_map<long long, long long> curr;

            if (array[i] == target) {
                ++total;
            }

            curr[array[i]] = 1;

            for (auto& [val, freq] : prev) {
                long long newAnd = val & array[i];

                if (newAnd == target) {
                    total += freq;
                }

                curr[newAnd] += freq;
            }

            prev = curr;
            ++i;
        }

        return total;
    }
};
