class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll kitna = 0;

        unordered_map<ll, ll> pichla;

        rep(i,0,n){
            kitna += himanshu138(nums[i], k, pichla);
            himanshu138mahajan(nums[i], k, pichla);
        }

        return kitna;
    }

private:
    ll himanshu138(ll hello, int k, unordered_map<ll,ll>& pichla) {
        ll kitna = 0;
        if (hello == k) {
            ++kitna;
        }
        for (auto& [uska, kitnahai] : pichla) {
            ll naya = uska & hello;
            if (naya == k) {
                kitna += kitnahai;
            }
        }
        return kitna;
    }

    void himanshu138mahajan(ll hello, int k, unordered_map<ll,ll>& pichla) {
        unordered_map<ll,ll> yewala;
        yewala[hello] = 1;

        for (auto& [uska, kitnahai] : pichla) {
            ll naya = uska & hello;
            yewala[naya] += kitnahai;
        }
        pichla = yewala;
    }
};
