class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        count = 0
        n = len(nums)

        prefix_count = defaultdict(int)

        for i in range(n):
            new_prefix_count = defaultdict(int)
            new_prefix_count[nums[i]] += 1

            for and_result in prefix_count:
                new_and_result = and_result & nums[i]
                new_prefix_count[new_and_result] += prefix_count[and_result]

            if k in new_prefix_count:
                count += new_prefix_count[k]

            prefix_count = new_prefix_count

        return count
