class Solution {
    public long countSubarrays(int[] nums, int k) {        
        int n = nums.length;
        long ans = 0;
        Map<Long, Long> map1 = new HashMap<>();
        Map<Long, Long> map2 = new HashMap<>();

        for(int num : nums){
            if(num == k){
                ans=ans+1;
            }
            map2.put((long)(num), (long)1);

            for(long key : map1.keySet()){
                if((key & (long)(num)) == k){
                    ans = ans+map1.get(key);
                }
                map2.put(key & (long)(num), map2.getOrDefault(key & (long)(num), (long)0)+map1.get(key));
            }
            map1 = new HashMap<>(map2);
            map2.clear();
        }
        return ans;
    }
}
