import java.util.HashMap;

class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long count = 0;

        HashMap<Long, Long> prev = new HashMap<>();

        for (int i = 0; i < n; ++i) {
            HashMap<Long, Long> curr = new HashMap<>(); 

            if (nums[i] == k) {
                ++count;
            }
            curr.put((long) nums[i], 1L);

            for (var j : prev.entrySet()) {
                long ans = j.getKey() & (long) nums[i];
                if (ans == k) {
                    count += j.getValue();
                }
                curr.merge(ans, j.getValue(), Long::sum);
            }

            prev = curr;
        }

        return count;
    }
}
