class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int size = array.size();
        long long ans = 0;
        unordered_map<long long, long long>p;
        for (int i=0;i<size;i++) {
            unordered_map<long long, long long> c; 
            if (array[i] == target) ans++;
            c[array[i]] = 1;
            for (auto& [value, f] : p) {
                long long f1 = value & array[i];
                if (f1 == target) ans += f;
                c[f1] += f;
            }
            p = c;
        }
        return ans;
    }
};
