class Solution {
#define ll long long
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll n = nums.size();
        ll ans = 0;
        map<ll, ll> mp;
        for(ll i=0; i<n; i++){
            map<ll, ll> temp;
            if(nums[i]==k)
                ans++;
            temp[nums[i]] = 1;
            for (auto j:mp) {
                ll val = j.first & nums[i];
                if(val == k)
                    ans = ans + j.second;
                temp[val] = temp[val] + j.second;
            }
            mp = temp;
        }
        return ans;
    }
};
