class Solution {
public:
        long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        map<long long, long long> mp;
        long long result = 0;
        long long i = 0;
        while(i<n){
            map<long long, long long> mp1;
            for (auto &v : mp) {
                long long x = v.first & nums[i];
                mp1[x] += v.second;
            }
            mp1[nums[i]]++;
            mp = mp1;
            if (mp.find(k) != mp.end()) {
                result += mp[k];
            }
            i++;
        }

        return result;
    }
};
