class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        unordered_map<long long, long long> previous;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> current;

            if (nums[i] == k) {
                count++;
            }

            current[nums[i]] = 1;

            for (const auto& it : previous) {
                long long andresult = it.first & nums[i];

                if (andresult == k) {
                    count += it.second;
                }

                current[andresult] += it.second;
            }

            previous = move(current);
        }

        return count;
    }
};
