class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int curr = 0;
        int n = nums.size();
        map<int, int> mp;
        for(int i=0; i<n; i++){
            if(nums[i] == k){
                ans++;
            }
            map<int, int> curr;
            curr[nums[i]]++;
            for(auto it: mp){
                if((it.first&nums[i]) == k){
                    ans += it.second;
                }
                curr[it.first&nums[i]] += it.second;
            }
            mp = curr;
        }

        return ans;
    }
};
