class Solution {
public:
    void calc(vector<vector<int>> &st,vector<int> &a){
        int n=a.size();
         for(int j=0;j<32;j++){
            for(int i=0;i<n;i++){
                if(((a[i]>>j)&1)==0)
                    st[j].push_back(i);
            }
        }
    }
    long long countSubarrays(vector<int>& a, int k) {
        vector<vector<int>> st(33);
        int n=a.size();
        long long ans=0;
        calc(st,a);
        for(int i=0;i<n;i++){
            int s=i,e=n;
            for(int j=0;j<32;j++){
                int f=0;
                if((1<<j)&k){
                    f=1;
                }
                auto ptr=lower_bound(st[j].begin(),st[j].end(),i);
                int k;
                if(ptr==st[j].end()){
                    k=n;
                }
                else
                    k=*ptr;
                if(f==0){
                    s=max(s,k);
                }
                else{
                    e=min(e,k);
                }

            }
            if(e>s)
            ans+=e-s;      
        }
        return ans;

    }
};
