class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        long long subarrayCount = 0;
        long long n = nums.size();
        for(int i=0;i<n;i++){
            if(nums[i]==target){
                subarrayCount++;
            }
        }
        map<long long, long long> currentAndMap;
        for (int i = 0; i < n; i++) {
            map<long long, long long> newAndMap;
            newAndMap[nums[i]]++;
            for (auto &entry : currentAndMap) {
                long long newAndValue = nums[i]&entry.first ;
                newAndMap[newAndValue] =newAndMap[newAndValue]+ entry.second;
                if (target==newAndValue) {
                    subarrayCount =subarrayCount+ entry.second;
                }
            }
            currentAndMap = newAndMap;         
        }
        return subarrayCount;
    }
};
