class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        Map<Long, Long> m = new HashMap<>();
        m.put((long) nums[0], 1L);
        long res = 0L;
        res += m.getOrDefault((long)k, 0L);
        for(int i = 1; i < n; i ++) {
            Map<Long, Long> t = new HashMap<>();
            t.put((long) nums[i], 1L);
            for(Map.Entry<Long, Long> e : m.entrySet()) {
                long key = e.getKey();
                long curKey = key & nums[i];
                long curVal = t.getOrDefault(curKey, 0L);
                t.put(curKey, e.getValue() + curVal);
            }
            m = t;
            res += m.getOrDefault((long)k, 0L);
        }
        return res;
    }
}
