class Solution {
public:
    void adder(vector<vector<long long>>& hash, long long x){
        for(long long j=0; j<32; j++){
            hash[j][x%2]++;
            x = x/2;
        }
    }
    void negaar(vector<vector<long long>>& hash,long long x){
        for(long long j=0; j<32; j++){
            hash[j][x%2]--;
            x = x/2;
        }
    }
    long long calc(vector<vector<long long>>& hash){
        long long ans = 0;
        for(long long j=0; j<32; j++){
            if(hash[j][0]){
                continue;
            }
            if(hash[j][1]){
                ans += pow(2,j);
            }
        }
        return ans; 
    }

    long long solve(vector<int>& nums, int k){
        long long n = nums.size();
        vector<vector<long long>> hash(32,vector<long long>(2,0));
        long long ans = 0;
        long long j = 0;
        for(long long i=0; i<n; i++){
            adder(hash,nums[i]);
            while(calc(hash)<k && i>=j){
                negaar(hash,nums[j]);
                j++;
            }
            if(calc(hash)>=k && i>=j){
                ans += (i-j+1);
            }
        }
        return ans; 
    }
    long long countSubarrays(vector<int>& nums, int k) {
        return solve(nums,k)-solve(nums,k+1);
    }
};
