class Solution {
    long long int solve(vector<int>&arr, int target){
        int size = arr.size();
        long long ans = 0;

        unordered_map<long long, long long> mp;

        for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> curr; 
            if (arr[i] == target) {
                ++ans;
            }
            curr[arr[i]] = 1;
            for (auto& [val, freq] : mp) {
                long long value = val & arr[i];
                if (value == target) {
                    ans += freq;
                }
                curr[value] += freq;
            }
            mp = curr;
        }
        return ans;
    }
public:
    long long countSubarrays(vector<int>& arr, int target) {
        return solve(arr, target);
    }
};
