#define ll long long
class Solution {
public:
    vector<ll> Bit(int x) {
        vector<ll> res(32);
        for (int i = 0; i < 32; i++) {
            res[i] = x % 2;
            x /= 2;
        }
        return res;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        ll n = nums.size();
        vector<ll> tbit(32);
        vector<vector<int>> v(32);
         for(int j = 0;j<32;j++)
        v[j].push_back(n);
        for (int i = n - 1; i >= 0; i--) {
            vector<ll> b = Bit(nums[i]);
            for (int j = 0; j < 32; j++)
                if (!b[j])
                    v[j].push_back(i);
        }
        vector<ll> b = Bit(k);
        for (int i = 0; i < n; i++) {
            int l = i, r = n;
            for (int j = 0; j < 32; j++) {
                if (b[j] && !v[j].empty())
                    r = min(r, v[j].back());
                if(!b[j])
                    l = max(l, v[j].back());
            }
            if(l<=r)
            ans += (r - l);
            vector<ll> x = Bit(nums[i]);
            for (int j = 0; j < 32; j++)
                if (!x[j])
                    v[j].pop_back();
        }
        return ans;
    }
};
