class Solution {
public:
#define ll long long
    long long countSubarrays(vector<int>& nums, int target) {
        ll ans = 0;
        unordered_map<ll, ll> mp;
        for (int i = 0; i < nums.size(); ++i) {
            unordered_map<ll, ll> cur; 
            if (nums[i] == target) {
                ++ans;
            }
            cur[nums[i]] = 1;
            for (const auto& [prv, count] : mp) {
                ll next = prv & nums[i];
                if (next == target) {
                    ans += count;
                }
                cur[next] += count;
            }
            mp = cur;
        }
        return ans;
    }
};
