class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<long long,long long> mp;
        long long count=0;
        for(int i=0;i<nums.size();i++){
            if(i==0){
                if(nums[i]==k) count++;
                mp[nums[i]]++;
                continue;
            }
            map<long long,long long> cur_mp;


            for(auto v:mp){
                if((nums[i]&v.first)==k){
                    count += v.second;
                }
                cur_mp[(nums[i]&v.first)]+=v.second;
            }

             cur_mp[nums[i]]++;
             if(k==nums[i]) count++;
             cur_mp[nums[i]];
            mp = cur_mp;
        }

        return count;
    }
};
