import java.util.HashMap;
import java.util.Map;

class Solution {
    class pair{
        int a;
        int b;
        public pair(int a,int b){
            this.a=a;
            this.b=b;
        }

    }
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long ans = 0;
        HashMap<Long, Long> prev = new HashMap<>();
        HashSet<Integer>aaaa=new HashSet();
        for (int i = 0; i < n; ++i) {
            HashMap<Long, Long> curr = new HashMap<>();
            if (nums[i] == k) {
                ++ans;
            }
            curr.put((long) nums[i], 1L);

            for (Map.Entry<Long, Long> entry : prev.entrySet()) {
                long value = entry.getKey();
                long frequemcy = entry.getValue();

                long newAnd = value & nums[i];
                if (newAnd == k) {
                    ans += frequemcy;
                }
                curr.put(newAnd, curr.getOrDefault(newAnd, 0L) + frequemcy);
            }
            prev = curr;
        }

        return ans;
    }
}
