#define ll long long
class Solution {
public:
    ll dfs(vector<int>& nums, map<int,ll>& mp,int k,int id,ll ct)
    {
        if(id==nums.size())
            return ct;
        ll sm=0;
        map<int,ll> temp; 
        int val=nums[id];
        if(val==k)
            sm++;
        temp[val]=1;
        for(auto it:mp)
        {
            ll curr=it.first&val;
            if (curr==k)
                sm+=it.second;
            temp[curr]+=it.second;
        }   
        mp=temp;
        return dfs(nums,mp,k,id+1,ct+sm);
    }
    long long countSubarrays(vector<int>& nums, int k) {
        map<int,ll> mp;
        return dfs(nums,mp,k,0,0);
    }
};
