class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {

        long long cnt =0;
        int n = arr.size();
        unordered_map<long long , long long>mp;

        for(int i =0;i<n;i++){
             unordered_map<long long , long long>curr;


            if(arr[i]==k) cnt++;
            curr[arr[i]] = 1;

            for(auto it:mp){
                long long newand = it.first&arr[i];
                if(newand==k) cnt+=it.second;
                 curr[newand]+=it.second;
            }
           mp = curr;


        }
         return cnt;
    }


};
