#define ll long long 
class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        stack<ll> st;
        ll n=arr.size(),cnt=0;;
        vector<ll> fwd(n),bwd(n);
        unordered_map<ll,ll> mp,temp;
        for(int i=n-1; i>=0; i--){
            if(st.empty()){
                st.push(i);
                bwd[i]=n;
            }
            else{
                while(!st.empty() && arr[st.top()]>=arr[i]){
                    st.pop();
                }
                if(st.empty()){
                    st.push(i);
                    bwd[i]=n;
                }
                else{
                    bwd[i]=st.top();
                }
            }
        }
        for(int i=0; i<n; i++){
            if(arr[i]==k){
                cnt++;
            }
            temp[arr[i]]=1;
            for(auto it:mp){
                ll chk=it.first&arr[i];
                if(chk==k){
                    cnt+=it.second;
                }
                temp[chk]+=it.second;
            }
            mp=temp;
            temp.clear();
        }
        return cnt;
    }
};
