class Solution
{
public:
    long long countSubarrays(vector<int> arr, int k)
    {
        long long ans = 0;
        int n = arr.size();
        map<int, long long> mp;

        for (int i = 0; i < n; i++)
        {
            map<int, long long> new_mp;
            for (auto j : mp)
                new_mp[(arr[i] & j.first)] = (1LL) * new_mp[(arr[i] & j.first)] + j.second;

            new_mp[arr[i]] = new_mp[arr[i]] + 1;
            ans = (1LL) * ans + new_mp[k];
            mp = new_mp;
        }

        return ans;
    }
};
