class Solution {
public:
    typedef long long ll;
    typedef map<ll,ll> map;

    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll ans = 0;
        map mp;

        for(int i = 0; i < n; i++) 
        {
            map now; 
            if (nums[i] == k)   ans++;
            now[nums[i]]++;

            for(auto it: mp) 
            {
                ll curr = it.first & nums[i];
                if(curr == k) ans += it.second;
                now[curr] += it.second;
            }
            mp = now;
        }
        return ans;
    }
};
