class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<long long,long long>mp;
        long long cnt=0;
        for(int i=0;i<n;i++){
            unordered_map<long long,long long>mp1;
            if(nums[i]==k) cnt++;
            mp1[nums[i]]=1;
            for(auto it:mp){
                long long res=it.first&nums[i];
                if(res==k) cnt+=it.second;

                mp1[res]+=it.second;
            }
            mp=mp1;
        }
        return cnt;
    }
};
