class Solution {
public:
    #define ll long long int
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll ans = 0;
        unordered_map<ll,ll> res;
        for (int i = 0; i < n; ++i) {
            unordered_map<ll,ll> cur; 
            if (nums[i] == k) ans++;
            cur[nums[i]] = 1;
            for (auto& [v, f] : res) {
                if ((v & nums[i]) == k) ans += f;
                cur[(v & nums[i])] += f;
            }
            res = cur;
        }
        return ans;
    }
};
