class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        long long ans=0;
        int n=arr.size();
        map<long long,long long> mp;
        for(int i=0;i<n;i++){
            map<long long,long long> farzi;
            int curr=arr[i];
            if(curr==k) ans++;
            farzi[arr[i]]++;

            for(auto it:mp){
                long long a=it.first;
                long long b=it.second;
                if((a&arr[i])==k) ans+=b;

                if((a&arr[i])>=k)
                farzi[(a&arr[i])]+=b;
            }
            mp=farzi;
            // cout<<ans<<endl;
        }
        return ans;
    }
};
